function UpdateDetails({ index,
    onChangeDay,
    onChangeBreakfast,
    onChangeLunch,
    onChangeDinner,
    onClickRemove, }) {
    // const [day, setDay] = useState()
    //this state is used to render customer or mess owner form conditionally
  const [number, setNumber] = useState(1);
    return (
        <ScrollView>
            <View
                style={{
                    flexDirection: 'row',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    marginTop: 20,
                }}>
                <Text style={{ marginLeft: 20, fontSize: 20, }}>Address :  </Text>

            </View>
            <TextInput
                style={styles.input}
                placeholder="Enter Address"
                onChangeText={txt => {
                    onChangeDay(txt);
                }}
            />
            <Text style={styles.text}>Phone : </Text>
            <TextInput
                style={styles.input}
                placeholder="Enter Phone no."
                onChangeText={txt => {
                    onChangeBreakfast(txt);
                }}
            />

            <Text style={styles.text}>Service :</Text>
            {/* <DropDownPicker
                open={open}
                value={value}
                items={items}
                setOpen={setOpen}
                setValue={setValue}
                setItems={setItems}

                theme="DARK"
