                mode="BADGE"
                badgeDotColors={["#e76f51", "#00b4d8", "#e9c46a", "#e76f51", "#8ac926", "#00b4d8", "#e9c46a"]}
            /> */}



            <Text style={styles.text}>Type :</Text>
            <TextInput
                style={styles.input}
                placeholder="Enter food item in Dinner"
                onChangeText={txt => {
                    onChangeDinner(txt);
                }}
            />

            <Text style={styles.text}>Trial :</Text>
            <TextInput
                style={styles.input}
                placeholder="Enter "
                onChangeText={txt => {
                    onChangeBreakfast(txt);
                }}
            />
            <Text style={styles.text}>Breakfast :</Text>
            <TextInput
                style={styles.input}
                placeholder="Enter food item in Breakfast"
                onChangeText={txt => {
                    onChangeBreakfast(txt);
                }}
            />
            <Text style={styles.text}>Pricing :</Text>
            <TextInput
                style={styles.input}
                placeholder="Enter Price for mess"
                onChangeText={txt => {
                    onChangeBreakfast(txt);
                }}
            />


            <Text style={styles.text}>Pricing :</Text>
            <TextInput
                style={styles.input}
                placeholder="Enter name of your mess"
                onChangeText={txt => {
                    onChangeBreakfast(txt);
                }}
            />
        </ScrollView>


    )
}

export default UpdateDetails;

const styles = StyleSheet.create({
    container: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginTop: 20,
    },
    input: {
        width: '90%',
        height: 50,
        borderWidth: 0.5,
        borderRadius: 10,
